import React, { useState } from 'react';
import {
  ChakraProvider,
  Box,
  Container,
  Input,
  Button,
  VStack,
  HStack,
  Text,
  Select,
  Checkbox,
  Card,
  CardBody,
  Badge,
  InputGroup,
  InputLeftElement,
  useToast,
} from '@chakra-ui/react';
import { SearchIcon } from '@chakra-ui/icons';
import axios from 'axios';

const API_URL = 'http://localhost:8000';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [fileType, setFileType] = useState('');
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');
  const [fuzzyMatch, setFuzzyMatch] = useState(true);

  const toast = useToast();

  const handleSearch = async () => {
    if (!query.trim()) {
      toast({
        title: '请输入搜索内容',
        status: 'warning',
        duration: 2000,
        isClosable: true,
      });
      return;
    }

    setLoading(true);
    try {
      const params = {
        q: query,
        fuzzy: fuzzyMatch,
        max_results: 10,
      };

      if (fileType) params.file_type = fileType;
      if (dateFrom) params.date_from = dateFrom;
      if (dateTo) params.date_to = dateTo;

      const response = await axios.get(`${API_URL}/api/search`, { params });
      setResults(response.data.results);
    } catch (error) {
      toast({
        title: '搜索失败',
        description: error.message,
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <ChakraProvider>
      <Container maxW="container.xl" py={8}>
        <VStack spacing={6} align="stretch">
          {/* 搜索框和按钮 */}
          <Card>
            <CardBody>
              <VStack spacing={4}>
                <InputGroup>
                  <InputLeftElement pointerEvents="none">
                    <SearchIcon color="gray.300" />
                  </InputLeftElement>
                  <Input
                    placeholder="输入搜索内容..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                  />
                </InputGroup>

                {/* 高级搜索选项 */}
                <HStack spacing={4} width="100%">
                  <Select
                    placeholder="文件类型"
                    value={fileType}
                    onChange={(e) => setFileType(e.target.value)}
                  >
                    <option value="txt">TXT</option>
                    <option value="md">Markdown</option>
                    <option value="py">Python</option>
                  </Select>

                  <Input
                    type="date"
                    placeholder="开始日期"
                    value={dateFrom}
                    onChange={(e) => setDateFrom(e.target.value)}
                  />

                  <Input
                    type="date"
                    placeholder="结束日期"
                    value={dateTo}
                    onChange={(e) => setDateTo(e.target.value)}
                  />

                  <Checkbox
                    isChecked={fuzzyMatch}
                    onChange={(e) => setFuzzyMatch(e.target.checked)}
                  >
                    模糊匹配
                  </Checkbox>
                </HStack>

                <Button
                  colorScheme="blue"
                  onClick={handleSearch}
                  isLoading={loading}
                  width="100%"
                >
                  搜索
                </Button>
              </VStack>
            </CardBody>
          </Card>

          {/* 搜索结果 */}
          <VStack spacing={4} align="stretch">
            {results.map((result) => (
              <Card key={result.doc_id}>
                <CardBody>
                  <VStack align="stretch" spacing={2}>
                    <HStack justify="space-between">
                      <Text fontWeight="bold">{result.doc_id}</Text>
                      <Badge colorScheme="blue">
                        相关度: {result.score.toFixed(4)}
                      </Badge>
                    </HStack>
                    <Text>{result.content}</Text>
                    <HStack>
                      <Badge>{result.file_type}</Badge>
                      <Badge colorScheme="green">
                        {new Date(result.last_modified).toLocaleString()}
                      </Badge>
                    </HStack>
                  </VStack>
                </CardBody>
              </Card>
            ))}
          </VStack>
        </VStack>
      </Container>
    </ChakraProvider>
  );
}

export default App;
